// import React from 'react';
import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';

// Think about putting the object in the return and using hooks
// to update the particular data we want to update

const ChartGraph = props => {
  const dataApi = props.resp;
  // const [posChartData, setPosDataChart] = useState({});
  // const [dataTr, setDataTr] = useState({});

  const chartData = {
    labels: ['label 1', 'label 2', 'label 3', 'label 4'],
    datasets: [
      {
        label: 'test label',
        data: [48, 35, 73, 82],
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)'
        ],
        borderWidth: 3
      }
    ]
  };

  return (
    <div>
      <h1> testing Charts</h1>
      <Line data={{ chartData }} />
      {/* <Line /> */}
    </div>
  );
};

export default ChartGraph;
