import React from 'react';
// import Chart from 'chart.js';

const Chart = props => {
  const data = props.resp;
  // console.log(data);

  const posChart = () => {
    data.map(t => {
      console.log(t);
    });
  };
  posChart();
  // console.log(chartHosp);

  return {
    initChartPositive() {
      // let myLineChart =
      new Chart(ctx, {
        type: 'line',
        data: {
          datasets: [
            {
              label: 'Active Cases in US',
              data: increaseChart,
              backgroundColor: 'rgba(34,45,0,0.5)'
            },
            {
              label: 'Positive Cases in US',
              data: newPosChart,
              backgroundColor: 'rgba(34,0,65,0.5)'
            }
          ],
          labels: monthLable,
          fontColor: 'rgb(166, 171, 179)'
        },
        options: {
          scales: {
            yAxes: [
              {
                ticks: {
                  // need to dynamiclly add the max, use a function
                  max: maxPosData,
                  min: 0,
                  stepSize: 100000,
                  fontColor: 'rgb(166, 171, 179)'
                }
              }
            ],
            xAxes: [
              {
                ticks: {
                  fontColor: 'rgb(166, 171, 179)'
                }
              }
            ]
          },
          legend: {
            labels: {
              fontColor: 'rgb(166, 171, 179)'
            }
          }
        }
      });
    }
  };
};

export default Chart;
